tinymce.PluginManager.add("fileupload", function(a, b) {
    a.addButton("example", {
        icon: "searchreplace",
        onclick: function() {
            a.windowManager.open({
                title: "Dosya Yükleyin/Seçin",
                url: "ajax.php?script=fileupload",
                width: 600,
                height: 400,
                buttons: [{
                    text: "Kapat",
                    onclick: "close"
                }]
            })
        }
    })
});